// HomePage.styled.jsx
import styled from "styled-components";

export const HomePageContainer = styled.div`
  display: flex;
  justify-content: center;
  flex-direction: column;
  align-items: center;
  font-family: Arial, sans-serif;
  background-color: #ffffff;
`;

export const Header = styled.header`
  margin-top: 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 1184px;
  height: 48px;
  //   padding: 20px;
  background-color: #ffffff;
  box-shadow: 0 1px 5px rgba(0, 0, 0, 0.1);
`;

export const Navigation = styled.nav`
  display: flex;
  gap: 20px;
`;

export const Registration = styled.nav`
  display: flex;
  gap: 20px;
`;

export const NavLink = styled.a`
  text-decoration: none;
  color: ${({ primary }) => (primary ? "#ffffff" : "#000000")};
  background-color: ${({ primary }) => (primary ? "#000000" : "transparent")};
  padding: ${({ primary }) => (primary ? "10px 20px" : "0")};
  border-radius: 5px;
  transition: background-color 0.3s;

  &:hover {
    background-color: ${({ primary }) => (primary ? "#333333" : "#f0f0f0")};
  }
`;

export const Content = styled.div`
  display: flex;
  //   justify-content: center
  flex-direction: row;
  //   align-items: center;
  //   max-width: 1200px;
  margin: 20px 64px 24px;
  gap: 24px;
`;

export const TitleContent = styled.div`
  display: flex;
  flex-direction: column;
  //   justify-content: center;
  //   align-items: center;
  width: 720px;
  height: 530px;
  border-radius: 30px;
  background-color: #f8f8f8;
`;

export const Title = styled.h1`
  display: block;
  width: 548px;
  height: 112px;
  font-size: 48px;
  font-weight: 500;
  line-height: 56px;
  margin-top: 98px;
  margin-left: 64px;
  span {
    font-style: italic;
    color: #f0b33c; // Highlighted color for "language"
  }
`;

export const SubTitle = styled.p`
  display: block;
  width: 471px;
  height: 66px;
  font-size: 16px;
  font-weight: 400;
  line-height: 22px;
  margin-top: 32px;
  margin-left: 64px;
`;

export const GetButton = styled.button`
  width: 267px;
  height: 60px;
  //   padding: 16px 88px 16px 88px;
  border-radius: 12px;
  font-size: 1em;
  color: #ffffff;
  background-color: #ff8c00;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #e07b00;
  }
`;

export const ImageContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 568px;
  height: 530px;
  border-radius: 30px;
  background-color: #f8f8f8;
`;

export const StatsContainer = styled.div`
  //   margin-top: 24px;
  display: flex;
  justify-content: space-around;
  align-items: center;
  padding: 30px;
  background-color: #ffffff;
  margin: 50px auto;
  max-width: 1200px;
  box-shadow: 0 1px 5px rgba(0, 0, 0, 0.1);
  border-radius: 10px;
`;

export const StatBox = styled.div`
  text-align: center;
`;

export const StatNumber = styled.div`
  font-size: 1.5em;
  font-weight: bold;
  color: #333333;
  margin-bottom: 10px;
`;

export const StatDescription = styled.div`
  font-size: 1em;
  color: #777777;
`;

export const LogoContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 8px; /* Space between logo and text */
`;

export const LogoImage = styled.img`
  width: 28px; /* Set the desired width */
  height: 28px; /* Set the desired height */
`;

export const LogoText = styled.p`
  font-size: 20px;
  font-weight: 500;
  color: var(--text-color);
  margin: 0;
`;
