// HomePage.jsx
import React, { useState, useRef, useEffect } from "react";
import {
  HomePageContainer,
  Header,
  Navigation,
  NavLink,
  NavLinkRegistr,
  Content,
  Title,
  SubTitle,
  GetButton,
  StatsContainer,
  StatBox,
  StatNumber,
  StatDescription,
  ImageContainer,
  LogoContainer,
  LogoImage,
  LogoText,
  Registration,
  TitleContent,
  RegButton,
} from "./HomePage.styled";
import Logo from "../../assets/svg/ukraine.svg";
import EmojiYellow from "../../assets/svg/yellow.svg";
import EmojiGreen from "../../assets/svg/green.svg";
import EmojiBlue from "../../assets/svg/blue.svg";
import EmojiBrown from "../../assets/svg/brown.svg";
import EmojiPink from "../../assets/svg/pink.svg";
import Theme from "components/Theme/Theme";
import styled from "styled-components";

// Styled SVG Component for Login Icon
const LoginIcon = styled.svg`
  width: 20px;
  height: 20px;
  fill: none;
  stroke: var(--btn-icon-bg);
`;

const HomePage = () => {
  const [showTheme, setShowTheme] = useState(false);
  const [currentTheme, setCurrentTheme] = useState(
    localStorage.getItem("currentTheme") || "yellow"
  );
  const loginIconRef = useRef(null);

  useEffect(() => {
    if (loginIconRef.current) {
      loginIconRef.current.style.stroke = "var(--btn-icon-bg)";
    }
  }, []);

  const getEmojiImage = () => {
    switch (currentTheme) {
      case "yellow":
        return EmojiYellow;
      case "green":
        return EmojiGreen;
      case "blue":
        return EmojiBlue;
      case "brown":
        return EmojiBrown;
      case "pink":
        return EmojiPink;
      default:
        return EmojiYellow;
    }
  };

  return (
    <HomePageContainer>
      <Header>
        <LogoContainer>
          <LogoImage src={Logo} alt="LearnLingo Logo" />
          <LogoText>LearnLingo</LogoText>
        </LogoContainer>
        <Navigation>
          <NavLink href="#">Home</NavLink>
          <NavLink href="#">Teachers</NavLink>
          <NavLink onClick={() => setShowTheme(true)}>
            {showTheme && (
              <Theme
                closeMenu={() => {
                  setShowTheme(false);
                  setCurrentTheme(localStorage.getItem("currentTheme"));
                }}
              />
            )}
            Theme
          </NavLink>
        </Navigation>
        <Registration>
          {/* Использование рефа без отображения иконки */}
          <svg ref={loginIconRef} style={{ display: "none" }}>
            <path />
          </svg>
          <NavLinkRegistr href="#">Log in</NavLinkRegistr>
          <RegButton>Registration</RegButton>
        </Registration>
      </Header>

      <Content>
        <TitleContent>
          <Title>
            Unlock your potential with the best <span>language</span> tutors
          </Title>
          <SubTitle>
            Embark on an Exciting Language Journey with Expert Language Tutors.
            Elevate your language proficiency to new heights by connecting with
            highly qualified and experienced tutors.
          </SubTitle>
          <GetButton>Get started</GetButton>
        </TitleContent>
        <ImageContainer>
          <img src={getEmojiImage()} alt="Emoji" />
        </ImageContainer>
      </Content>

      <StatsContainer>
        <StatBox>
          <StatNumber>32,000 +</StatNumber>
          <StatDescription>
            Experienced<br></br> tutors
          </StatDescription>
        </StatBox>
        <StatBox>
          <StatNumber>300,000 +</StatNumber>
          <StatDescription>
            5-star tutor<br></br> reviews
          </StatDescription>
        </StatBox>
        <StatBox>
          <StatNumber>120 +</StatNumber>
          <StatDescription>
            Subjects<br></br> taught
          </StatDescription>
        </StatBox>
        <StatBox>
          <StatNumber>200 +</StatNumber>
          <StatDescription>
            Tutor<br></br> nationalities
          </StatDescription>
        </StatBox>
      </StatsContainer>
    </HomePageContainer>
  );
};

export default HomePage;
