import React, { useEffect, useRef, useState } from "react";
import ThemeStyled from "./Theme.styled";

const colorTheme = ["yellow", "green", "blue", "brown", "pink"];

const Theme = () => {
  const [showMenu, setShowMenu] = useState(false);
  const [currentTheme, setCurrentTheme] = useState(
    localStorage.getItem("currentTheme") || "yellow"
  );
  // const [selectedTheme, setSelectedTheme] = useState("yellow");

  const menuRef = useRef(null);
  const textRef = useRef(null);
  const svgRef = useRef(null);

  const handleThemeChange = (theme) => {
    // dispatch(updateTheme({ theme }));
    setCurrentTheme(theme);
  };

  useEffect(() => {
    document.documentElement.setAttribute("data-theme", currentTheme);
    localStorage.setItem("currentTheme", currentTheme);
  }, [currentTheme]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        menuRef.current &&
        !menuRef.current.contains(event.target) &&
        !textRef.current.contains(event.target) &&
        !svgRef.current.contains(event.target)
      ) {
        setShowMenu(false);
      }
    };

    const handleKeyPress = (event) => {
      if (event.key === "Escape") {
        setShowMenu(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    document.addEventListener("keydown", handleKeyPress);

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
      document.removeEventListener("keydown", handleKeyPress);
    };
  }, []);

  return (
    <ThemeStyled>
      <div className="theme-selector">
        <select value={selectedTheme} onChange={handleThemeChange}>
          {colorTheme.map((theme) => (
            <option key={theme} value={theme}>
              {theme}
            </option>
          ))}
        </select>
      </div>
      {/* Additional content based on selected language */}
    </ThemeStyled>
  );
};

export default Theme;
