// import { useEffect } from "react";
import { createPortal } from "react-dom";
import Icon from "../Icon/Icon";
import { Opacity, Modalcont, ModalClose } from "./Modal.styled";

const Modal = ({ children, width, height, onClose, top, left }) => {
  const modalRoot = document.getElementById("modal-root");

  const handleCloseKeyPress = (event) => {
    if (event.code === "Escape") {
      onClose();
      return () => document.removeEventListener("keydown", handleCloseKeyPress);
    }
  };
  document.addEventListener("keydown", handleCloseKeyPress);

  const handleOutsideClick = (event) => {
    if (event.target === event.currentTarget) {
      onClose();
      return () => document.removeEventListener("keydown", handleCloseKeyPress);
    }
  };

  return createPortal(
    <Opacity onClick={handleOutsideClick}>
      <Modalcont
        $Width={width}
        $Height={height}
        onClick={(event) => event.stopPropagation()}
        $top={top}
        $left={left}
      >
        <ModalClose type="button" aria-label="Close modal" onClick={onClose}>
          <Icon
            width={18}
            height={18}
            strokeColor={"var(--text-color)"}
            name={"close"}
          />
        </ModalClose>
        {children}
      </Modalcont>
    </Opacity>,
    modalRoot
  );
};

export default Modal;
