// Theme.jsx
import React, { useEffect, useRef, useState } from "react";
import ThemeStyled from "./Theme.styled";

const Theme = ({ closeMenu }) => {
  const [currentTheme, setCurrentTheme] = useState(
    localStorage.getItem("currentTheme") || "yellow"
  );

  const menuRef = useRef(null);
  const textRef = useRef(null);
  const svgRef = useRef(null);

  const handleThemeChange = (theme) => {
    setCurrentTheme(theme);
  };

  const handleClose = () => {
    closeMenu(); // Call the function passed from the parent component
  };

  useEffect(() => {
    // Apply the current theme to the document
    document.documentElement.setAttribute("data-theme", currentTheme);
    // Store the current theme in local storage
    localStorage.setItem("currentTheme", currentTheme);
    console.log(currentTheme);
  }, [currentTheme]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      // Check if the click is outside the menu
      if (
        menuRef.current &&
        !menuRef.current.contains(event.target) &&
        !textRef.current.contains(event.target) &&
        !svgRef.current.contains(event.target)
      ) {
        handleClose(); // Uncomment this line to close the menu on outside click
      }
    };

    const handleKeyPress = (event) => {
      if (event.key === "Escape") {
        handleClose();
      }
    };

    // Add event listeners for mouse click and key press
    document.addEventListener("mousedown", handleClickOutside);
    document.addEventListener("keydown", handleKeyPress);

    return () => {
      // Cleanup the event listeners
      document.removeEventListener("mousedown", handleClickOutside);
      document.removeEventListener("keydown", handleKeyPress);
    };
  }, []);

  return (
    <ThemeStyled>
      <div className="theme-selector" ref={menuRef}>
        <ul>
          <li
            onClick={() => handleThemeChange("yellow")}
            className={currentTheme === "yellow" ? "active" : ""}
          >
            yellow
          </li>
          <li
            onClick={() => handleThemeChange("green")}
            className={currentTheme === "green" ? "active" : ""}
          >
            green
          </li>
          <li
            onClick={() => handleThemeChange("blue")}
            className={currentTheme === "blue" ? "active" : ""}
          >
            blue
          </li>
          <li
            onClick={() => handleThemeChange("brown")}
            className={currentTheme === "brown" ? "active" : ""}
          >
            brown
          </li>
          <li
            onClick={() => handleThemeChange("pink")}
            className={currentTheme === "pink" ? "active" : ""}
          >
            pink
          </li>
        </ul>
      </div>
    </ThemeStyled>
  );
};

export default Theme;
