// HomePage.styled.jsx
import styled from "styled-components";

export const HomePageContainer = styled.div`
  display: flex;
  justify-content: center;
  flex-direction: column;
  align-items: center;
  font-family: Arial, sans-serif;
  background-color: #f8f9fa;
`;

export const Header = styled.header`
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 1184px;
  height: 48px;
  padding: 20px;
  background-color: #ffffff;
  box-shadow: 0 1px 5px rgba(0, 0, 0, 0.1);
`;

export const Navigation = styled.nav`
  display: flex;
  gap: 20px;
`;

export const NavLink = styled.a`
  text-decoration: none;
  color: ${({ primary }) => (primary ? "#ffffff" : "#000000")};
  background-color: ${({ primary }) => (primary ? "#000000" : "transparent")};
  padding: ${({ primary }) => (primary ? "10px 20px" : "0")};
  border-radius: 5px;
  transition: background-color 0.3s;

  &:hover {
    background-color: ${({ primary }) => (primary ? "#333333" : "#f0f0f0")};
  }
`;

export const Content = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 50px;
  max-width: 1200px;
  margin: 0 auto;
`;

export const Title = styled.h1`
  font-size: 2.5em;
  font-weight: bold;
  margin-bottom: 20px;

  span {
    font-style: italic;
    color: #f0b33c; // Highlighted color for "language"
  }
`;

export const SubTitle = styled.p`
  font-size: 1.2em;
  margin-bottom: 30px;
  line-height: 1.5;
  color: #555555;
`;

export const CTAButton = styled.button`
  padding: 15px 30px;
  font-size: 1em;
  color: #ffffff;
  background-color: #ff8c00;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #e07b00;
  }
`;

export const StatsContainer = styled.div`
  display: flex;
  justify-content: space-around;
  align-items: center;
  padding: 30px;
  background-color: #ffffff;
  margin: 50px auto;
  max-width: 1200px;
  box-shadow: 0 1px 5px rgba(0, 0, 0, 0.1);
  border-radius: 10px;
`;

export const StatBox = styled.div`
  text-align: center;
`;

export const StatNumber = styled.div`
  font-size: 1.5em;
  font-weight: bold;
  color: #333333;
  margin-bottom: 10px;
`;

export const StatDescription = styled.div`
  font-size: 1em;
  color: #777777;
`;

export const ImageContainer = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const Image = styled.img`
  max-width: 100%;
  height: auto;
  border-radius: 50%;
`;
const LogoContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 10px; /* Space between logo and text */
`;

const LogoImage = styled.img`
  width: 50px; /* Set the desired width */
  height: 50px; /* Set the desired height */
`;

const LogoText = styled.p`
  font-size: 1.5em;
  font-weight: bold;
  color: #333333;
  margin: 0;
`;
