// Theme.jsx
import React, { useEffect, useRef, useState } from "react";
import ThemeStyled from "./Theme.styled";

const Theme = ({ closeMenu }) => {
  const themes = ["yellow", "green", "blue", "brown", "pink"];
  const [currentTheme, setCurrentTheme] = useState(
    localStorage.getItem("currentTheme") || "yellow"
  );

  const menuRef = useRef(null);

  const handleThemeChange = (theme) => {
    setCurrentTheme(theme);
  };

  const handleClose = () => {
    closeMenu();
  };

  useEffect(() => {
    document.documentElement.setAttribute("data-theme", currentTheme);
    localStorage.setItem("currentTheme", currentTheme);
  }, [currentTheme]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      // Check if the click is outside the menu, textRef, and svgRef
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        handleClose();
      }
    };

    const handleKeyPress = (event) => {
      if (event.key === "Escape") {
        handleClose();
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    document.addEventListener("keydown", handleKeyPress);

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
      document.removeEventListener("keydown", handleKeyPress);
      handleClose();
    };
  }, []);

  return (
    <ThemeStyled>
      <div className="theme-selector" ref={menuRef}>
        <ul>
          {themes.map((theme) => (
            <li
              key={theme}
              onClick={() => handleThemeChange(theme)}
              className={currentTheme === theme ? "active" : ""}
            >
              {theme}
            </li>
          ))}
        </ul>
      </div>
    </ThemeStyled>
  );
};

export default Theme;
